export default function Card(props) {
  /* let profileBadge;
  if (props.openSpots === 0) {
    profileBadge = "SOLD OUT";
  } else if (props.status === "Friends") {
    profileBadge = "FRIENDS";
  } */
  console.log(props);
  return (
    <div className="ui link cards">
      <div className="card">
        <div className="image">
          {<div className="card--badge">{}</div>}
          <img src={props.item[0].avatar} alt="avatar-matt" />
        </div>
        <div className="content">
          <div className="header">{props.name}</div>
          <div className="meta">
            <a>{props.status}</a>
          </div>
          <div className="description">{props.description}</div>
        </div>
        <div className="extra content">
          <span className="right floated">{props.dateJoined}</span>
          <span>
            <i className="user icon"></i>
            {props.friends[0].number + " " + props.friends[0].status}
          </span>
        </div>
      </div>
    </div>
  );
}
